import React, { Component } from 'react';
import logo from './img/logo.png'
import foto1 from './img/back.png'
import precio from './img/precio.png'
import axios from 'axios';
class ConferenceForm extends Component {
constructor(props) {
    super(props);
    this.state = {
      nombre: '',
      apellidos: '',
      selectedCountry: '', // Estado para almacenar el país seleccionado
      countries: [], // Estado para almacenar la lista de países
      afiliacion: '',
      correo: '',
      telefono: '',
      oficio: 'Estudiante',
      esMiembroIEEE: false,
      numeroMembresia: '',
      tipoParticipacion: 'Autor',
      extrap1: '',
      extrap2: '',
      cobro: 0,
      mostrarMensajeExito: false, 
      mensaje: '',
      numDocuments: 1,
      articles: [
        {
          articleNumber: '', 
          pages: '',
        },
      ],
    };
  }


  handleSubmit = (event) => {
    event.preventDefault();

    const formData = {
      nombre: this.state.nombre,
      apellidos: this.state.apellidos,
      pais: this.state.selectedCountry,
      afiliacion: this.state.afiliacion,
      correo: this.state.correo,
      telefono: this.state.telefono,
      oficio: this.state.oficio,
      tipoParticipacion: this.state.tipoParticipacion,
      esMiembroIEEE: this.state.esMiembroIEEE,
      numeroMembresia: this.state.numeroMembresia,
      numArt:this.state.numDocuments,
      articulos:this.state.articles
    };
    console.log("boton presionado")
  axios.post('http://localhost:80/registro', formData)
      .then(response => {
        // Procesar la respuesta del servidor
        console.log('Respuesta del servidor:', response.data);
      })
      .catch(error => {
        // Manejar errores, si los hay
        console.error('Error al enviar datos al servidor:', error);
      });

      axios.post('http://localhost:80/cobro', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
        .then((response) => {
          // Almacena el valor de cobro en el estado
          this.setState({ cobro: response.data.cobro });
        })
        .catch((error) => {
          console.error('Error al obtener el valor de cobro:', error);
        });


        this.setState({ mostrarMensajeExito: true });

        // Después de cierto tiempo, ocultar el mensaje
        setTimeout(() => {
          this.setState({ mostrarMensajeExito: false });
        }, 10000); 

  }
  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }
  componentDidMount() {
    // Realiza una solicitud a la API para obtener la lista de países
    fetch('https://restcountries.com/v3.1/all')
    .then((response) => response.json())
    .then((data) => {
      // Ordena la lista de países por nombre común
      const sortedCountries = data.sort((a, b) => a.name.common.localeCompare(b.name.common));
      this.setState({ countries: sortedCountries });
    })
    .catch((error) => {
      console.error('Error al cargar la lista de países', error);
    });
  }

  handleArticleInputChange = (index, fieldName, value) => {
    this.setState((prevState) => {
      const articles = [...prevState.articles];
      //articles[index][fieldName] = value;
      //return { articles };
      // Asegurarse de que el objeto para este artículo exista
    if (!articles[index]) {
      articles[index] = {
        title: '',
        articleNumber: '',
        pages: '',
      };
    }

    articles[index][fieldName] = value;
    return { articles };
    });
  };
  handleCountryChange = (event) => {
    this.setState({ selectedCountry: event.target.value });
  };


  handlePagar = (event) => {
    event.preventDefault();

    const formData = {
      nombre: this.state.nombre,
      apellidos: this.state.apellidos,
      pais: this.state.pais,
      afiliacion: this.state.afiliacion,
      correo: this.state.correo,
      telefono: this.state.telefono,
      oficio: this.state.oficio,
      tipoParticipacion: this.state.tipoParticipacion,
      esMiembroIEEE: this.state.esMiembroIEEE,
      numeroMembresia: this.state.numeroMembresia,
      numArt:this.state.numDocuments,
      articulos:this.state.articles
    };
    axios.post('http://localhost:80/proceso_pago', formData)
    .then(async (response) => {
      console.log('Respuesta del servidor:', response.data);
      console.log("FUNCIONO SERVER:", response.status)
      // Verificar si la respuesta es exitosa
      if (response.status === 200) {
        // Obtener la URL de checkout desde la respuesta
        const checkoutURL = response.data.checkoutURL;
        console.log("FUNCIONO RETORNO")
        // Abrir una nueva pestaña o ventana con la URL de checkout
        window.open(checkoutURL, "_blank");
      } else {
        console.error('Error en el proceso de pago:', response.statusText);
      }
    })
    .catch(error => {
      console.error('Error al enviar datos al servidor:', error);
    });
/*
    const interval = setInterval(() => {
      consultarEstadoCobro((mensaje) => {
        setMensaje(mensaje);
      });
    }, 30000);

    // Detener la consulta después de 20 minutos (20 minutos = 20 minutos * 60 segundos * 1000 milisegundos)
    const stopInterval = setTimeout(() => {
      clearInterval(interval);
      console.log('Consulta de estado del Cobro detenida.');
    }, 20 * 60 * 1000);

    setIntervalId(interval);
*/

  }



  render() {
    const { numDocuments, articles, selectedCountry, countries } = this.state;
    return (


  <div>  
    <header className="header">
      <div className="logo-container">
     <img src={logo} alt="Logo" className="logo"/>
      </div>
      <div className="register">
       Registro conferencia IEEE C3
      </div>
    </header>
    <div class="contenido">
      <div class="formulario">
        <form onSubmit={this.handleSubmit}>
        <label>
            Nombres
            <br />
            <input
              type="text"
              name="nombre"
              value={this.state.nombre}
              onChange={this.handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Apellidos
            <br />
            <input
              type="text"
              name="apellidos"
              value={this.state.apellidos}
              onChange={this.handleInputChange}
              required
            />
          </label>
          <br />
          <label>
          País
          <br />
          <select
            name="selectedCountry"
            value={selectedCountry}
            onChange={this.handleCountryChange}
            required
          >
            <option value="">Selecciona un país</option>
            {countries.map((country) => (
              <option key={country.cca2} value={country.name.common}>
                {country.name.common}
              </option>
            ))}
          </select>
        </label>
          <br />

          <label>
            Afiliación institucional
            <br />
            <input
              type="text"
              name="afiliacion"
              value={this.state.afiliacion}
              onChange={this.handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Correo electrónico
            <br />
            <input
              type="email"
              name="correo"
              value={this.state.correo}
              onChange={this.handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Teléfono
            <br />
            <input
              type="tel"
              name="telefono"
              value={this.state.telefono}
              onChange={this.handleInputChange}
              required
            />
          </label>
          <br />

          <label>
            Oficio
            <br />
            <select
              name="oficio"
              value={this.state.oficio}
              onChange={this.handleInputChange}
            >
              <option value="Estudiante">Estudiante</option>
              <option value="Profesional">Profesional</option>
            </select>
          </label>
          <br />
          <label>
              Miembro IEEE
              <input
                type="checkbox"
                name="esMiembroIEEE"
                checked={this.state.esMiembroIEEE}
                onChange={() => this.setState({ esMiembroIEEE: !this.state.esMiembroIEEE })}
              />
            </label>
            <br />

            {this.state.esMiembroIEEE && (
              <label>
                Número de Membresía IEEE
                <br />
                <input
                  type="text"
                  name="numeroMembresia"
                  value={this.state.numeroMembresia}
                  onChange={this.handleInputChange}
                />
              </label>
            )}
            <br />

            <label>
        Tipo de participación
        <br />
        <input
          type="radio"
          name="tipoParticipacion"
          value="Autor"
          checked={this.state.tipoParticipacion === 'Autor'}
          onChange={this.handleInputChange}
        />
        Autor   
        <input
          type="radio"
          name="tipoParticipacion"
          value="Asistente"
          checked={this.state.tipoParticipacion === 'Asistente'}
          onChange={this.handleInputChange}
        />
        Asistente
      </label>
      <br />

      {this.state.tipoParticipacion === 'Autor' && (
        <div>
      <div>
        <label>
          Número de artículos a presentar
          <br />
          <input
            type="number"
            name="numDocuments"
            value={numDocuments}
            onChange={this.handleInputChange}
          />
        </label>
        <br />

        {Array.from({ length: numDocuments }).map((_, index) => (
          <div key={index}>
            <label>
              Número del artículo {index + 1}
              <br />
              <input
                type="text"
                name={`articles[${index}].articleNumber`}
                value={articles[index] ? articles[index].articleNumber : ''}
                onChange={(event) =>
                  this.handleArticleInputChange(
                    index,
                    'articleNumber',
                    event.target.value // Se cambió el nombre del campo
                  )
                }
                required
              />
            </label>
            <br />
            <label>
              Cantidad de Páginas del Artículo {index + 1}
              <br />
              <input
                type="number"
                name={`articles[${index}].pages`}
                value={articles[index] ? articles[index].pages : ''}
                onChange={(event) =>
                  this.handleArticleInputChange(
                    index,
                    'pages',
                    event.target.value
                  )
                }
                required
              />
            </label>
            <br />
          </div>
        ))}
      </div>
        </div>
      )}

          <button type="submit">Confirmar</button>

          {this.state.mostrarMensajeExito && (
        <div style={{ color: 'yellow', fontSize: '12px', marginTop: '10px' }}>
          Información guardada exitosamente
        </div>
          )}
        </form>
      </div>
         <div class="fotos">   
        <img src={foto1} alt="foto" className="conferencia"/>

        <div class="cobro">

          <div class="datocobro">
            <h2>El total a pagar es: </h2>
            <h2> {this.state.cobro} USD </h2>
            <button class='pago' onClick={this.handlePagar} type="submit">Pagar</button>
            <div>{this.state.mensaje && <p>{this.state.mensaje}</p>}</div>
          </div>
          <div class="logprecio">
          <img src={precio} alt="foto" className="preciolog"/>
          </div>
        </div>
        </div>

    </div>
  </div>
    );
  }
}

export default ConferenceForm;



async function consultarEstadoCobro(updateMensaje) {
  try {
    const response = await fetch('http://localhost:80/consultar_estado_cobro', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    });

    if (response.status === 200) {
      const cobro = await response.json();
      if (cobro.estado === 1) {
        updateMensaje('¡El pago ha sido exitoso!');
      } else if (cobro.estado === 2) {
        updateMensaje('El pago ha sido denegado.');
      } else {
        updateMensaje('El pago está siendo procesado.');
      }
    } else {
      console.error('Error al consultar el estado del Cobro:', response.statusText);
      updateMensaje('Error al consultar el estado del cobro.');
    }
  } catch (error) {
    console.error('Error al consultar el estado del Cobro:', error);
    updateMensaje('Error al consultar el estado del cobro.');
  }
}
