async function consultarEstadoCobro(updateMensaje) {
  try {
    const response = await fetch('http://localhost:80/consultar_estado_cobro', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    });

    if (response.status === 200) {
      const cobro = await response.json();
      if (cobro.estado === 1) {
        updateMensaje('¡El pago ha sido exitoso!');
      } else if (cobro.estado === 2) {
        updateMensaje('El pago ha sido denegado.');
      } else {
        updateMensaje('El pago está siendo procesado.');
      }
    } else {
      console.error('Error al consultar el estado del Cobro:', response.statusText);
      updateMensaje('Error al consultar el estado del cobro.');
    }
  } catch (error) {
    console.error('Error al consultar el estado del Cobro:', error);
    updateMensaje('Error al consultar el estado del cobro.');
  }
}
