import React from 'react';
import logo from './img/logo.png'
import foto1 from './img/back.png'
import precio from './img/precio.png'
import axios from 'axios';
const PagosExtras = () => {
    
    class ConferenceForm extends Component {
        constructor(props) {
            super(props);
            this.state = {
              nombre: '',
              apellidos: '',
              pais: '',
              afiliacion: '',
              correo: '',
              telefono: '',
              oficio: 'Estudiante',
              esMiembroIEEE: false,
              numeroMembresia: '',
              tipoParticipacion: 'Autor',
              tituloArticulo1: '', // Campo para el primer tÃ­tulo
              tituloArticulo2: '', 
              extrap1: '',
              extrap2: '',
              cobro: 0,
            };
          }
          handleSubmit = (event) => {
            event.preventDefault();
            const formData = {
              nombre: this.state.nombre,
              apellidos: this.state.apellidos,
              pais: this.state.pais,
              afiliacion: this.state.afiliacion,
              correo: this.state.correo,
              telefono: this.state.telefono,
              oficio: this.state.oficio,
              tipoParticipacion: this.state.tipoParticipacion,
              esMiembroIEEE: this.state.esMiembroIEEE,
              numeroMembresia: this.state.numeroMembresia,
              articulo1: this.state. tituloArticulo1,
              articulo2: this.state. tituloArticulo2,
              extrap1: this.state.extrap1,
              extrap2: this.state.extrap2,
            };
            console.log("boton presionado")
          axios.post('http://localhost:80/registro', formData)
              .then(response => {
                // Procesar la respuesta del servidor
                console.log('Respuesta del servidor:', response.data);
              })
              .catch(error => {
                // Manejar errores, si los hay
                console.error('Error al enviar datos al servidor:', error);
              });
        
              axios.post('http://localhost:80/cobro', formData, {
                headers: {
                  'Content-Type': 'application/json',
                },
              })
                .then((response) => {
                  // Almacena el valor de cobro en el estado
                  this.setState({ cobro: response.data.cobro });
                })
                .catch((error) => {
                  console.error('Error al obtener el valor de cobro:', error);
                });
            
          }
          handleInputChange = (event) => {
            const { name, value } = event.target;
            this.setState({ [name]: value });
          }
        
          handlePagosExtrasClick =(event) => {
        
            
          }
        
          render() {
            return (
              
              
          <div>  
            <header className="header">
              <div className="logo-container">
             <img src={logo} alt="Logo" className="logo"/>
              </div>
              <div className="register">
                PÃ¡gina de Registro
              </div>
            </header>
            <div class="contenido">
              <div class="formulario">
                <form onSubmit={this.handleSubmit}>
                
                  <label>
                    Oficio
                    <br />
                    <select
                      name="oficio"
                      value={this.state.oficio}
                      onChange={this.handleInputChange}
                    >
                      <option value="Estudiante">Estudiante</option>
                      <option value="Profesional">Profesional</option>
                    </select>
                  </label>
                  <br />
                  <label>
                      Miembro IEEE
                      <input
                        type="checkbox"
                        name="esMiembroIEEE"
                        checked={this.state.esMiembroIEEE}
                        onChange={() => this.setState({ esMiembroIEEE: !this.state.esMiembroIEEE })}
                      />
                    </label>
                    <br />
        
                    {this.state.esMiembroIEEE && (
                      <label>
                        NÃºmero de MembresÃ­a IEEE
                        <br />
                        <input
                          type="text"
                          name="numeroMembresia"
                          value={this.state.numeroMembresia}
                          onChange={this.handleInputChange}
                        />
                      </label>
                    )}
                    <br />
        
                    <label>
                Tipo de participaciÃ³n
                <br />
                <input
                  type="radio"
                  name="tipoParticipacion"
                  value="Autor"
                  checked={this.state.tipoParticipacion === 'Autor'}
                  onChange={this.handleInputChange}
                />
                Autor   
                <input
                  type="radio"
                  name="tipoParticipacion"
                  value="Asistente"
                  checked={this.state.tipoParticipacion === 'Asistente'}
                  onChange={this.handleInputChange}
                />
                Asistente
              </label>
              <br />
        
              {this.state.tipoParticipacion === 'Autor' && (
                <div>
                  <label>
                    TÃ­tulo del ArtÃ­culo 1 (Obligatorio)
                    <br />
                    <input
                      type="text"
                      name="tituloArticulo1"
                      value={this.state.tituloArticulo1}
                      onChange={this.handleInputChange}
                      required
                    />
                  </label>
                  <br />
                  <label>
                  <input
                        type="checkbox"
                        name="extrap1"
                        checked={this.state.extrap1}
                        onChange={() => this.setState({ extrap1: !this.state.extrap1 })}
                      />
                      El artÃ­culo tiene mÃ¡s de 6 pÃ¡ginas             
                    </label>
                    <br />
        
                  <label>
                    TÃ­tulo del ArtÃ­culo 2 (Opcional)
                    <br />
                    <input
                      type="text"
                      name="tituloArticulo2"
                      value={this.state.tituloArticulo2}
                      onChange={this.handleInputChange}
                    />
                  </label>
                  <br />
                  <label>
                  <input
                        type="checkbox"
                        name="extrap2"
                        checked={this.state.extrap2}
                        onChange={() => this.setState({ extrap2: !this.state.extrap2 })}
                      />
                      El artÃ­culo tiene mÃ¡s de 6 pÃ¡ginas             
                    </label>
                    <br />
                </div>
              )}
        
                  <button type="submit">Confirmar</button>
        
                  <button type="button" onClick={this.handlePagosExtrasClick}>Pagos Extras</button>
        
                </form>
              </div>
                 <div class="fotos">   
                <img src={foto1} alt="foto" className="conferencia"/>
        
                <div class="cobro">
                
                  <div class="datocobro">
                    <h2>El total a pagar es: </h2>
                    <h2> {this.state.cobro} USD </h2>
                    <button class='pago' type="submit">Pagar</button>
                  </div>
                  <div class="logprecio">
                  <img src={precio} alt="foto" className="preciolog"/>
                  </div>
                </div>
                </div>
        
            </div>
          </div>
            );
          }
        }
        
};

export default PagosExtras;
